import {Modal} from 'react-bootstrap';
import Pill from './pills/pill';
import axios from 'axios';
// import { data } from 'jquery';


export default function IntendedFieldOfStudyField(){
    const [show, setShow] = useState(false);
    const [data, setData] = useState({
                                      level_of_edu: "",
                                      field_of_study:"",
                                    });
    const [subjects, setSubjects] = useState([]);
    const [loading, setLoading] = useState(false);


    // Function handling text input for Intended field of study
    const onChangeIFoS = (e) => {
        var name = e.target.name;
        var value = e.target.value;
        setIFoS({[name]:value});
    }

    // Function called when subjects field is changed
    const onChangeSubjects = (e) => {
        var value = e.target.value;
        setSubjects([...subjects, value]);
    }

    // Remove element from subjects state array
    const remove = (item) => {
        const index = subjects.indexOf(item)
        subjects.splice(subjects, 1);
        setSubjects([...subjects])
    }

    // Submit function
    const onSubmit = async (e) => {
        e.preventDefault();

        try {
            setLoading(true);
            const res = await axios.post(`http://localhost:5000/admin/fis/add`,)
        } catch (error) {
            setLoading(false);
            console.log('Error: ' + error);
        }
        console.log(subjects);
    }



    // console.log(data.field_of_study)
    // console.log(subjects)
    return(
        <React.Fragment>

                <div className="btn btn-primary btn-sm" onClick={ ()=>setShow(true) }><i className="fa fa-user-plus mr-2"></i>Field of Study</div>

            <Modal
                show={show}
                onHide={()=>setShow(false)}
                aria-labelledby="example-custom-modal-styling-title"
                centered
            >
                <Modal.Body>
                    {/* <input /> */}
                    <div className="">
                        <div className="container-fluid">
                            <div className="d-flex justify-content-center">
                                <p style={{fontFamily:'quicksand', fontSize:'14px', fontWeight:'bold'}}>Create Intended Field of Study</p>
                            </div>
                            <label>Level:</label>
                            <input onChange={onChangeLevel} type="text" name="level_of_edu" value={ifis.level_of_edu} className="form-control text-small" placeholder="Level of Education..."/>
                            <label>Name of Field:</label>
                            <input onChange={onChangeIFoS} type="text" name="field_of_study" value={ifis.field_of_study} className="form-control text-small" placeholder="Intended field of Study"/>
                            <label className="mt-2">Select Subjects:</label>
                            <select onChange={onChangeSubjects} name="subjects" className="form-control text-small mb-3">
                                <option value="null">Select</option>
                                <option value="mathematics">Mathematics</option>
                                <option value="physics">Physics</option>
                                <option value="chemistry">Chemistry</option>
                                <option value="biology">Biology</option>
                                <option value="geography">Geography</option>
                                <option value="economics">Economics</option>
                                <option value="accounting">Accounting</option>
                                <option value="history">History</option>
                                <option value="general knowledge & personal development">General Knowledge & Personal Development</option>
                            </select>

                                {/* Submit Button */}
                            <button className="btn btn-primary mb-3" onClick={onSubmit} disabled={loading} style={{width:'100%'}}> Create { loading ? <i className="fa fa-spinner fa-spin"></i> : "" }</button>
                            <div className="container mb-4">
                                <div className="row mx-3 mt-2">
                                    {/* Place pills here */}
                                    { renderPill() }
                                </div>
                            </div>
                        </div>
                    </div>
                </Modal.Body>
            </Modal>
        </React.Fragment>
    );

    // Render the pills
    function renderPill(){
       return subjects.map(item => (
            <Pill text={item} pop={remove}/>
        ));
    }
}
