        //         </div>
        //     </div>
        // </div>
        <React.Fragment>
            {
                spinner ? <div className="d-flex justify-content-center"><i className="fa fa-spinner fa-spin"></i></div> : data.map((data) => {
                        return (
                            <GenServiceCard data={data}/>
                        )})
            }
        </React.Fragment>
    );
}

export default EASService;
