import React from 'react';
import '../comp.scss';

export default function Pills({text, remove}) {
    return(
        <React.Fragment>
            <div className="pill ml-2 pl-2 mr-2 ">{text} <i style={{color:'red'}} className="mt-2 mr-2" onClick={remove(text)}>X</i></div>
        </React.Fragment>
    );
}
